import { useSelector, useDispatch } from "react-redux";
import {
  fetchFrutti,
  aggiungiFrutto,
  eliminaFrutto,
  modificaFrutto
} from "../../redux/fruttiSlice";
import { useState, useEffect } from "react";

import classes_button from '../UI/button/MyButton.module.css'


import MyButton from "../UI/button/MyButton";

const Ezercizio = () => {
  const frutti = useSelector(state => state.frutti);
  const dispatch = useDispatch();

  const [nome, setNome] = useState("");
  const [categoria, setCategoria] = useState("");
  const [modificaId, setModificaId] = useState(null);

  // üì• carica i dati al primo render
  useEffect(() => {
    dispatch(fetchFrutti());
  }, [dispatch]);

  const handleAggiungi = () => {
    if (nome && categoria) {
      dispatch(aggiungiFrutto({ nome, categoria }));
      setNome("");
      setCategoria("");
    }
  };

  const handleModifica = (item) => {
    setNome(item.nome);
    setCategoria(item.categoria);
    setModificaId(item.id);
  };

  const handleSalva = () => {
    if (nome && categoria && modificaId) {
      dispatch(modificaFrutto({ id: modificaId, nome, categoria }));
      setNome("");
      setCategoria("");
      setModificaId(null);
    }
  };

  const toggleSidebar = () => {
    const sidebar = document.querySelector('.sidebar');
    sidebar.classList.toggle('sidebarDisplayNone');
  };



  return (
    <>
      <div className="container">
          <div className="sidebar sidebarDisplayNone">
              <div className="categories">
                  <ul>
                      <li><a href="">–≤—Å–µ</a></li>
                  </ul>
              </div>
          </div>

          <div className="main-content wide-content">

            <nav className="top-nav">
              <ul className="top-nav-links">
                  <li><a href="" className="nav-link"> –∑–∞–º–µ—Ç–∫–∏ </a></li>
                  <li><a href="" className="nav-link"> —Å—Ç–∞—Ç—å–∏ </a></li>
                  <li><a href="" className="nav-link"> –¥—Ä—É–∑—å—è </a></li>
                  <li><a href="" className="nav-link"> –Ω–æ–≤–æ—Å—Ç–∏ </a></li>
                  <li><a href="" className="nav-link"> –∑–∞–≥—Ä—É–∑–∏—Ç—å </a></li>
                  <li><a href="" className="nav-link"> –≥—Ä–∞—Ñ–∏–∫ </a></li>
                  <li><a href="" className="nav-link"> –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏—è </a></li>
                  <li><a href="" className="nav-link"> –ª–æ–≥–∏–Ω </a></li>
                  <li><a href="" className="nav-link"> –ø–æ—Å—Ç </a></li>
                  <li><a href="" className="nav-link"> –≤–æ—Ç—Å–∞–ø </a></li>
              </ul>
            </nav>


          <div id="poloski" title="visible Menu" onClick={toggleSidebar}>
            –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
          </div>


            <h2>üçé Lista </h2>

            <input
              value={nome}
              onChange={(e) => setNome(e.target.value)}
              placeholder="Nome"
            />
            <input
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
              placeholder="Categoria"
            />
            {modificaId ? (
              <button onClick={handleSalva}>üíæ Salva</button>
            ) : (
              <button onClick={handleAggiungi}>‚ûï Aggiungi</button>
            )}


              <div className="article-list">

                {frutti.map(item => (
                  <div  className="article-item" key={item.id}>
                    {item.nome} - {item.categoria}{" "}
                    <div className="actions">
                      <button onClick={() => handleModifica(item)}>‚úèÔ∏è</button>
                      <button className={classes_button.myBtn} onClick={() => dispatch(eliminaFrutto(item.id))}>‚ùå</button>
                    </div>
                    <button className="toggle-btn" >–ß–∏—Ç–∞—Ç—å –¥–∞–ª–µ–µ</button>
                  </div>
                ))}

              </div>

              <nav>
                  <ul className="pagination cursorPointer">
                      <li className="page-link">
                          <a href="">First</a>
                      </li>
                      <li className="page-link">
                          <a href="">Prev</a>
                      </li>
                      <li className="page-link">
                          <a href="">Next</a>
                      </li>
                      <li className="page-link">
                          <a href="">Last</a>
                      </li>
                  </ul>
              </nav>
          </div>
      </div>

    </>
  );
};

export default Ezercizio;
