import { useSelector, useDispatch } from "react-redux";
import {
  fetchFrutti,
  aggiungiFrutto,
  eliminaFrutto,
  modificaFrutto,
  setCurrentPage
} from "../../redux/fruttiSlice";
import { useState, useEffect } from "react";

const Ezercizio = () => {
  const dispatch = useDispatch();
  const frutti = useSelector(state => state.frutti.lista);
  const currentPage = useSelector((state) => state.frutti.currentPage);

  const [nome, setNome] = useState("");
  const [categoria, setCategoria] = useState("");
  const [modificaId, setModificaId] = useState(null);

  // 📥 carica i dati al primo render
  useEffect(() => {
    dispatch(fetchFrutti());
  }, [dispatch]);

  const handleAggiungi = () => {
    if (nome && categoria) {
      dispatch(aggiungiFrutto({ nome, categoria }));
      setNome("");
      setCategoria("");
    }
  };

  const handleModifica = (item) => {
    setNome(item.nome);
    setCategoria(item.categoria);
    setModificaId(item.id);
  };

  const handleSalva = () => {
    if (nome && categoria && modificaId) {
      dispatch(modificaFrutto({ id: modificaId, nome, categoria }));
      setNome("");
      setCategoria("");
      setModificaId(null);
    }
  };

  const toggleSidebar = () => {
    const sidebar = document.querySelector('.sidebar');
    sidebar.classList.toggle('sidebarDisplayNone');
  };

  const itemsPerPage = 2;
  const totalPages = Math.ceil(frutti.length / itemsPerPage);

  // 🧮 Calcola gli elementi da mostrare
  const indexOfLast = currentPage * itemsPerPage;
  const indexOfFirst = indexOfLast - itemsPerPage;
  const fruttiVisibili = frutti.slice(indexOfFirst, indexOfLast);

  // 🔘 Gestione cambio pagina
  const cambiaPagina = (numero) => {
    dispatch(setCurrentPage(numero));
  };

  return (
    <>
      <div className="container">
        <div className="sidebar sidebarDisplayNone">
          <div className="categories">
            <ul>
              <li><a href="">все</a></li>
            </ul>
          </div>
        </div>

        <div className="main-content wide-content">
          <nav className="top-nav">
            <ul className="top-nav-links">
              <li><a href="" className="nav-link"> заметки </a></li>
              <li><a href="" className="nav-link"> статьи </a></li>
              <li><a href="" className="nav-link"> друзья </a></li>
              <li><a href="" className="nav-link"> новости </a></li>
              <li><a href="" className="nav-link"> загрузить </a></li>
              <li><a href="" className="nav-link"> график </a></li>
              <li><a href="" className="nav-link"> авторизация </a></li>
              <li><a href="" className="nav-link"> логин </a></li>
              <li><a href="" className="nav-link"> пост </a></li>
              <li><a href="" className="nav-link"> вотсап </a></li>
            </ul>
          </nav>

          <div id="poloski" title="visible Menu" onClick={toggleSidebar}>
            категории
          </div>

          <h2>🍎 Lista </h2>

          <input
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            placeholder="Nome"
          />
          <input
            value={categoria}
            onChange={(e) => setCategoria(e.target.value)}
            placeholder="Categoria"
          />
          {modificaId ? (
            <button onClick={handleSalva}>💾 Salva</button>
          ) : (
            <button onClick={handleAggiungi}>➕ Aggiungi</button>
          )}

          <div className="article-list">
            {fruttiVisibili.map(item => (
              <div className="article-item" key={item.id}>
                <div className="item-info">
                  {item.nome} – {item.categoria}
                </div>

                <div className="actions">
                  <button onClick={() => handleModifica(item)}>✏️</button>
                  <button onClick={() => dispatch(eliminaFrutto(item.id))}>❌</button>
                </div>

                <button className="toggle-btn">Читать далее</button>
              </div>
            ))}
          </div>

          <nav>
            <ul className="pagination cursorPointer">
              <li className="page-item">
                <button className="page-link" onClick={() => cambiaPagina(1)}>First</button>
              </li>
              <li className="page-item">
                <button className="page-link" onClick={() => cambiaPagina(currentPage - 1)} disabled={currentPage === 1}>Prev</button>
              </li>
              <li className="page-item">
                <button className="page-link" onClick={() => cambiaPagina(currentPage + 1)} disabled={currentPage === totalPages}>Next</button>
              </li>
              <li className="page-item">
                <button className="page-link" onClick={() => cambiaPagina(totalPages)}>Last</button>
              </li>
            </ul>
          </nav>
        </div>
      </div>
    </>
  );
};

export default Ezercizio;
