import { useContext, useState } from 'react';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

import MyButton from '../../components/UI/button/MyButton';
import MyInput from '../../components/UI/input/MyInput';
import { AuthContext } from '../../context/';

const Login = () => {
  const { setIsAuth } = useContext(AuthContext);
  const navigate = useNavigate();
  const [error, setError] = useState('');

  const {
    register,
    handleSubmit,
    formState: { errors }
  } = useForm();

  const onSubmit = async (data) => {
    try {
      const response = await axios.post('http://localhost:3001/api/login', data);

      if (response.data.message === 'Login riuscito') {
        setIsAuth(true);
        localStorage.setItem('auth', 'true');
        setError('');
        navigate('/esercizio');
      }
    } catch (err) {
      console.error(err);
      setError('Неверный логин или пароль');
    }
  };

  const bcrypt = require('bcrypt');
bcrypt.hash("12345", 10).then(console.log);

  return (
    <div className="form__wrapper">
      <div>
        <h3>Страница для логина</h3>

        <form onSubmit={handleSubmit(onSubmit)}>
          <input
            type="text"
            placeholder="Введите логин"
            {...register('username', { required: 'Логин обязателен' })}
          />
          {errors.username && <p style={{ color: 'red' }}>{errors.username.message}</p>}

          <input
            type="password"
            placeholder="Введите пароль"
            {...register('password', { required: 'Пароль обязателен' })}
          />
          {errors.password && <p style={{ color: 'red' }}>{errors.password.message}</p>}

          <button>Войти</button>
        </form>

        {error && <p style={{ color: 'red' }}>{error}</p>}
      </div>
    </div>
  );
};

export default Login;
