import { NavLink } from 'react-router-dom';
import { useContext } from 'react';
import MyButton from '../../UI/button/MyButton';
import { AuthContext } from '../../../context';
import classes from './Navbar.module.css'

const Navbar = () => {
  const { isAuth, setIsAuth } = useContext(AuthContext);

  const logout = () => {
    setIsAuth(false);
    localStorage.removeItem('auth');
  };

  return (
    <div className={classes.navbar}>
      <div className={classes.navbar__links}>

        <NavLink
          to="/esercizio" 
          className={({ isActive }) => (isActive ? classes.active : classes.navbar_link)}
          exact="true"
        >
          esercizio
        </NavLink>

        <NavLink
          to="/" 
          className={({ isActive }) => (isActive ? classes.active : classes.navbar_link)}
          exact="true"
        >
          Home
        </NavLink>

        <NavLink
          to="/about" 
          className={({ isActive }) => (isActive ? classes.active : classes.navbar_link)}
        >
          About
        </NavLink>

        <NavLink
          to="/dialogs" 
          className={({ isActive }) => (isActive ? classes.active : classes.navbar_link)}
        >
          Dialogs
        </NavLink>
        
        <NavLink 
          className={({ isActive }) => (isActive ? classes.active : classes.navbar_link)}
          to="/users" 
        >
          Users
        </NavLink>
        
        <NavLink 
          className={({ isActive }) => (isActive ? classes.active : classes.navbar_link)}
          to="/posts" 
        >
          Posts
        </NavLink>

        {isAuth ? (
          <p className={classes.navbar_link}  onClick={logout}>
            Logout
          </p>
        ) : (
          <NavLink 
          className={({ isActive }) => (isActive ? classes.active : classes.navbar_link)}
            to="/login" 
          >
            Login
          </NavLink>
        )}
      </div>
    </div>
  );
};

export default Navbar;
