import { useSelector, useDispatch } from "react-redux";
import {
  fetchFrutti,
  aggiungiFrutto,
  eliminaFrutto,
  modificaFrutto
} from "../../redux/fruttiSlice";
import { useState, useEffect } from "react";

import classes_button from '../UI/button/MyButton.module.css'


import MyButton from "../UI/button/MyButton";

const Ezercizio = () => {
  const frutti = useSelector(state => state.frutti);
  const dispatch = useDispatch();

  const [nome, setNome] = useState("");
  const [categoria, setCategoria] = useState("");
  const [modificaId, setModificaId] = useState(null);

  // 📥 carica i dati al primo render
  useEffect(() => {
    dispatch(fetchFrutti());
  }, [dispatch]);

  const handleAggiungi = () => {
    if (nome && categoria) {
      dispatch(aggiungiFrutto({ nome, categoria }));
      setNome("");
      setCategoria("");
    }
  };

  const handleModifica = (item) => {
    setNome(item.nome);
    setCategoria(item.categoria);
    setModificaId(item.id);
  };

  const handleSalva = () => {
    dispatch(modificaFrutto({ id: modificaId, nome, categoria }));
    setNome("");
    setCategoria("");
    setModificaId(null);
  };

  return (
    <div>
      <h2>🍎 Lista </h2>

      <input
        value={nome}
        onChange={(e) => setNome(e.target.value)}
        placeholder="Nome"
      />
      <input
        value={categoria}
        onChange={(e) => setCategoria(e.target.value)}
        placeholder="Categoria"
      />
      {modificaId ? (
        <button onClick={handleSalva}>💾 Salva</button>
      ) : (
        <button onClick={handleAggiungi}>➕ Aggiungi</button>
      )}

      <ul>
        {frutti.map(item => (
          <li key={item.id}>
            {item.nome} - {item.categoria}{" "}
            <button onClick={() => handleModifica(item)}>✏️</button>
            <button className={classes_button.myBtn} onClick={() => dispatch(eliminaFrutto(item.id))}>❌</button>
          </li>
        ))}
      </ul>


      <div className="container">
          <div className="sidebar sidebarDisplayNone">
              <div className="categories">
                  <ul>
                      <li><a href="">все</a></li>
                  </ul>
              </div>
          </div>

          <div className="main-content wide-content">
              <div id ="poloski" title="visible Menu"> категории </div>

              <div className="article-list">
                  <div className="add-item">
                      <p>Add a new task</p>
                  </div>

                  <div className="article-item">
                      <div className="actions">
                        <button >✏️</button>

                        <form method="POST" onsubmit="">
                          <input type = "submit" name = "" value = "🗑️"></input>
                        </form>
                      </div>

                      <button className="toggle-btn" >Читать далее</button>
                  </div>
              </div>

              <nav>
                  <ul className="pagination cursorPointer">
                      <li className="page-link">
                          <a href="">First</a>
                      </li>
                      <li className="page-link">
                          <a href="">Prev</a>
                      </li>
                      <li className="page-link">
                          <a href="">Next</a>
                      </li>
                      <li className="page-link">
                          <a href="">Last</a>
                      </li>
                  </ul>
              </nav>
          </div>
      </div>

    </div>
  );
};

export default Ezercizio;
